package Day3;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 12;
		
		a+=2;
		System.out.println("add"+" "+a);//14
		
		int a1 = 12;
		
		a1-=2;
		System.out.println("sub"+" "+a1);//10
		
		int a2 = 12;
		a2*=2;
		System.out.println("mul"+" "+a2);//24
		
		int a3 = 12;
		a3/=2;
		System.out.println("devi"+" "+a3); //6
		
		int a4 = 12;
		a4%=2;
		System.out.println("modu"+" "+a4); // 0


	}

}
